'use strict'

const express = require('express')
const bodyParser = require('body-parser')
const mongoose = require('mongoose')
mongoose.connection.close()
const app = express()

const Product = require('../modelos/product.js')

const port = process.env.PORT || 3001
app.use(bodyParser.urlencoded({extended:false}))
app.use(bodyParser.json())

app.get('/api/producto', function (req, res) {
  res.status(200).send({products:['a','b']})
})
app.get('/api/producto/:id', function (req, res) {
  let productID = req.params.id
  Product.findById(productID,(err,product)=>
  {
    if(err)
    {
        return res.status(500).send({message:'Error !!'})
    }else if(!product)
    {
        return res.status(404).send({message:'No se ha encontrado el producto'})
    }else
    {
         return res.status(200).send({product})
    }
  })
})
app.delete('/api/producto/:id',function(req,res)
{
    let productId = req.params.id

    Product.findById(productId,(err,product)=>
    {
        if(err)
        {
            return res.status(500).send({message:'Error'})
        }else if(!product)
        {
            return res.status(404).send({message:'No se ha encontrado el producto'})
        }else
        {
            product.remove(err=>
            {
                if(err)return res.status(500).send({message:'Error'})
                return res.status(200).send({message:'El producto ha sido eliminado'})
            })
        }
    })
})
app.post('/api/producto', function (req, res) {
    console.log('POST api/product')
    console.log(req.body)

    let product = new Product()
    product.name = req.body.name
    product.picture = req.body.picture
    product.price =req.body.price
    product.category=req.body.category
    product.description=req.body.description

    product.save((err,productStored)=>
    {
        if(err)
        {
            res.status(500).send({message: 'Error al guardar en la BD'})
        }else
        {
            res.status(200).send({product: productStored})
        }
    })
})

